import '@fortawesome/fontawesome-free/js/all';


import './app/timer.js';


// Enable tooltips
$(document).on('turbolinks:load', function() {
    //     if ($("#myAreaChart").length) {
    //         myCharts.start();
    //     }
    $(function () {
        $('[data-toggle="tooltip"]').tooltip()
    })
});

// Notify - Toastify-JS
import Toastify from 'toastify-js'
document.addEventListener("turbolinks:load", () => {
    Toastify({
        text: "This is a toast",
        duration: 3000,
        // destination: "https://github.com/apvarun/toastify-js",
        // newWindow: true,
        // close: true,
        gravity: "top", // `top` or `bottom`
        position: "center", // `left`, `center` or `right`
        // backgroundColor: "linear-gradient(to right, #4e73df, #224abe)",
        // background: "linear-gradient(to right, #4e73df, #96c93d)",
        stopOnFocus: true, // Prevents dismissing of toast on hover
        onClick: function(){} // Callback after click
    }).showToast();

    // if ($(".toastify").length) {
    //     $(".toastify").style.background = "linear-gradient(to right, #4e73df, #96c93d)";
    //     console.log('entrou no toastify');
    // }
});
