$(document).on('turbolinks:load', function() {
    if ($("#dataTable").length) {
        console.log('Entrou no customDataTables');
        $('#dataTable').DataTable({
          //dom: 'Bfrtip',
          dom: 'Bflrtip',
          responsive: true,
          buttons: ['csv', 'excel'],
          "pagingType": "simple_numbers",
          "language": {
              "url": "//cdn.datatables.net/plug-ins/1.10.16/i18n/Portuguese-Brasil.json",
            //   buttons: {
            //       pageLength: {
            //           _: "Exibir %d registros"
            //       }
            //   }
          }
        });
      }
});
