$(document).on('turbolinks:load', function() {
jQuery( document ).ready(function( $ ) {
    if ($("#datatable").length) {
        $('#datatable').DataTable( {
            // dom: 'Bfrtip',
            responsive: true,
            // lengthMenu: [
            //     [ 10, 25, 50, -1 ],
            //     [ '10 registros', '25 registros', '50 registros', 'Exibir todos' ]
            // ],
            buttons: [
                // {
                //     extend: 'csv',
                //     text: 'Exportar Excel'
                // },
                // {
                //     extend: 'copy',
                //     text: 'Copiar'
                // },
                {
                    extend: 'print',
                    text: 'Imprimir',
                    exportOptions: {
                        columns: [ 0, 1, 2 ] //Definir quais colunas estarão visíveis para impressão
                    }
                },
                // [
                //     'pageLength'
                // ]

            ],
            "pagingType": "simple_numbers",
            // "pagingType": "simple_numbers",
            // "dom": '<"topcustom"lfr>t<"bottomcustom"ip>',
            "language": {
                "url": "//cdn.datatables.net/plug-ins/1.10.16/i18n/Portuguese-Brasil.json",
                buttons: {
                    pageLength: {
                        _: "Exibir %d registros"
                    }
                }
            }
        });
    }
});
});
